import React, { useState } from 'react';
import './App.css';

const Button = ({ label, onClick, className }) => (
  <button className={className} onClick={onClick}>{label}</button>
);

const Display = ({ value }) => <div className="display">{value}</div>;

const Calculator = () => {
  const [input, setInput] = useState('');

  const handleClick = (label) => {
    if (label === '=') {
      try {
        setInput(eval(input).toString());
      } catch {
        setInput('Error');
      }
    } else if (label === 'C') {
      setInput('');
    } else {
      setInput(input + label);
    }
  };

  return (
    <div className="calculator">
      <Display value={input} />
      <div className="number-buttons">
        {['1', '2', '3', '4', '5', '6', '7', '8', '9', '0', '.'].map((label) => (
          <Button key={label} label={label} onClick={() => handleClick(label)} className="number-button" />
        ))}
      </div>
      <div className="operation-buttons">
        <Button label="+" onClick={() => handleClick('+')} className="operation-button" />
        <Button label="-" onClick={() => handleClick('-')} className="operation-button" />
        <Button label="*" onClick={() => handleClick('*')} className="operation-button" />
        <Button label="/" onClick={() => handleClick('/')} className="operation-button" />

      </div>
    </div>
  );
};

function App() {
  return <Calculator />;
}

export default App;
