import React, { useState } from 'react';
import './App.css';

const Button = ({ label, onClick, className }) => (
  <button className={className} onClick={onClick}>{label}</button>
);

const Display = ({ value }) => <div className="display">{value}</div>;

const Calculator = () => {
  const [input, setInput] = useState('');

  const handleClick = (label) => {
    if (label === '=') {
      setInput(eval(input).toString());
    } else if (label === 'C') {
      setInput('');
    } else {
      setInput(input + label);
    }
  };

  return (
    <div className="calculator">
      <Display value={input} />
      {['1', '2', '3', '4', '5', '6', '7', '8', '9', '0', 'C'].map((label) => (
        <Button key={label} label={label} onClick={() => handleClick(label)} className="number-button" />
      ))}
      {['+', '-', '*', '/', '='].map((label) => (
        <Button key={label} label={label} onClick={() => handleClick(label)} className="operation-button" />
      ))}
    </div>
  );
};

function App() {
  return <Calculator />;
}

export default App;
