import React from 'react';
import './TaskItem.css';

const TaskItem = ({ task, editTask, deleteTask }) => {
  return (
    <li className="task-item">
      <div>
        <h3>{task.taskName}</h3>
        <p>Data: {task.dueDate || 'Não definida'}</p>
        <p>Status: {task.isCompleted ? 'Concluída' : 'Pendente'}</p>
      </div>
      <div className="task-item-actions">
        <button onClick={() => editTask(task)}>Editar</button>
        <button onClick={() => deleteTask(task)}>Deletar</button>
      </div>
    </li>
  );
};

export default TaskItem;
